      {/*      <Text ml="4" fontWeight="medium"> */}
      {/*        Configurações */}
      {/*      </Text> */}
      {/*    </Link> */}
      {/*  </Stack> */}
      {/* </Box> */}
    </Stack>
  )
}
export default SidebarNav
