import React, { useState, useEffect, useRef, useCallback } from "react"
import {
  Box,
  Button,
  Grid,
  Heading,
  Stack,
  Image,
  Text,
  HStack,
} from "@chakra-ui/react"
import Webcam from "react-webcam"
import ImagePicker from "react-image-picker"
import "./styles.css"

const Atendimento = () => {
  const webcamRef = useRef(null)
  const [audio, setAudio] = useState(false)
  const [images, setImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])

  useEffect(() => {
    console.log("selImages", selectedImages)
  }, [selectedImages])

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current?.getScreenshot()
    setImages((img) => [...img, imageSrc])
  }, [webcamRef])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }
  function handleCreateReport() {
    console.log("report", selectedImages)
  }

  return (
    <Box flex="1" w="100%" flexDir="row" p="6" mb={6} marginBottom={5}>
      <Box borderRadius="10" bg="gray.500" w="100%" mb={6} p={2}>
        <Text fontWeight="bold">Victor Mesquita</Text>
        <Text fontWeight="sm" color="gray.300">
          Victor
        </Text>
      </Box>
      <Grid templateColumns="repeat(2, 1fr)" gap={5} px={4} h="100%">
        <Box borderRadius="10">
          <Webcam audio={audio} ref={webcamRef} screenshotFormat="image/jpeg" />
          <HStack py={2} spacing={4}>
            <Button onClick={() => capture()}>Capturar Imagem</Button>
            <Button onClick={() => capture()}>Gravar VÃ­deo </Button>
            {selectedImages ? (
              <Button onClick={() => handleCreateReport()}>Emitir Laudo</Button>
            ) : null}
          </HStack>
        </Box>
        <Box bg="gray.500" borderRadius="10" p={6}>
          <ImagePicker
            className="images"
            images={images.map((image, i) => ({ src: image, value: i }))}
            multiple
            onPick={(selImgs) => handleSelectedImages(selImgs)}
          />
        </Box>
      </Grid>
    </Box>
  )
}

export default Atendimento
