import React, { useCallback, useEffect, useState, useRef } from "react"
import {
  Row,
  Col,
  PageHeader,
  Card,
  Button,
  Input,
  Space,
  Select,
  Empty,
  Form,
  Dropdown,
  Menu,
  Descriptions,
  Skeleton,
  message,
} from "antd"
import { useHistory, useLocation } from "react-router-dom"
import { FileTextOutlined } from "@ant-design/icons"
import { Editor } from "@tinymce/tinymce-react"
import { ImagePicker } from "../../components"

const { TextArea } = Input

const { Option } = Select

const Laudo = () => {
  const navigation = useHistory()
  const location = useLocation()
  const editorRef = useRef(null)
  const [component, setComponent] = useState([])
  const [laudoImages, setLaudoImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])
  const [selectedItems, setSelectedItems] = useState([])
  const [text, setText] = useState("")
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    setLaudoImages(location.state.images)
  }, [location, text])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }

  const log = () => {
    if (editorRef.current) {
      console.log(editorRef.current.getContent())
    }
  }

  const textToHtml = (text) => {
    const elem = document.createElement("div")
    return text
      .split(/\n\n+/)
      .map(
        (paragraph) =>
          `<p>${paragraph
            .split(/\n+/)
            .map((line) => {
              elem.textContent = line
              return elem.innerHTML
            })
            .join("<br/>")}</p>`
      )
      .join("")
  }

  const onClick = ({ key }) => {
    message.info(`Click on item ${key}`)
    switch (key) {
      case 1:
        setText((laudo) => [...laudo, "1. Teste\n 2. Teste \n 3. Teste"])
        break
      case 2:
        setText((laudo) => [...laudo, "1. Teste\n 2. Teste \n 3. Teste"])
        break
      default:
        setText((laudo) => [...laudo, "1. Teste\n 2. Teste \n 3. Teste"])
        break
    }
  }

  const menu = (
    <Menu>
      <Menu.Item onClick={onClick} key="2">
        Orofaringe
      </Menu.Item>
    </Menu>
  )
  return (
    <Row>
      <Col span={24}>
        <PageHeader onBack={() => window.history.back()} title="Emitir Laudo" />
      </Col>

      <Row span={24}>
        <Descriptions title="Dados do Paciente">
          {loading ? (
            <Skeleton paragraph={{ rows: 2 }} />
          ) : (
            <>
              <Descriptions.Item label="Solicitante">
                Dr. Áureo Cangussu
              </Descriptions.Item>
              <Descriptions.Item label="Nome">Paulo Victor</Descriptions.Item>
              <Descriptions.Item label="Data de Nascimento">
                10/05/1997
              </Descriptions.Item>
              <Descriptions.Item label="Telefone">
                (98)9 9174-1075
              </Descriptions.Item>
              <Descriptions.Item label="Emissor">Teste</Descriptions.Item>
            </>
          )}
        </Descriptions>
      </Row>

      <Row gutter={[24]}>
        <Col span={16}>
          <Card
            title="Tópicos"
            extra={
              <Dropdown overlay={menu} placement="bottomCenter" arrow>
                <Button>Tópicos</Button>
              </Dropdown>
            }
          >
            <Editor
              ref={editorRef}
              /* eslint-disable-next-line no-return-assign */
              onInit={(evt, editor) => (editorRef.current = editor)}
              outputFormat="text"
              init={{
                height: 500,
                resize: false,
                menubar: false,
                plugins: [
                  "advlist autolink lists link image",
                  "charmap print preview anchor help",
                  "searchreplace visualblocks code",
                  "insertdatetime media table paste wordcount",
                ],
                toolbar:
                  "undo redo | formatselect | bold italic | alignleft aligncenter alignright | bullist numlist outdent indent | help | searchreplace",
              }}
              value={text}
              onEditorChange={(newText) => setText(newText)}
            />
            {/* eslint-disable-next-line react/button-has-type */}
            <button onClick={log}>Log editor content</button>
          </Card>
        </Col>

        <Col span={8}>
          <Card
            title="Imagens"
            bodyStyle={{ overflowY: "scroll", maxHeight: "500px" }}
          >
            {laudoImages.length >= 1 ? (
              <ImagePicker
                inline
                images={laudoImages.map((image, i) => ({
                  src: image.src,
                  value: i,
                }))}
                multiple
                onPick={(selImgs) => handleSelectedImages(selImgs)}
              />
            ) : (
              <Empty />
            )}
          </Card>
        </Col>
      </Row>
    </Row>
  )
}

export default Laudo
