import React from "react"
import {
  Box,
  Icon,
  Link,
  Stack,
  Text,
  useBreakpointValue,
} from "@chakra-ui/react"
import { Link as RDomLink } from "react-router-dom"
import { RiDashboardLine } from "react-icons/ri"
import { GrConfigure } from "react-icons/gr"
import "./styles.css"
import { useSidebarDrawer } from "../../../contexts"

const SidebarNav = () => {
  const { isOpen, onClose } = useSidebarDrawer()
  const isDrawerSidebar = useBreakpointValue({
    base: true,
    lg: false,
  })
  return (
    <div className="grid-container">
      <Stack
        spacing="12"
        align="flex-start"
        className={isOpen ? null : "sidebar_small"}
      >
        <Text fontSize="3xl" fontWeight="bold" letterSpacing="tight" w="64">
          Sisac Image
        </Text>
        <Box>
          <Text fontWeight="bold">Consultório</Text>
          <Stack spacing="4" mt="8" align="stretch">
            <Link
              as={RDomLink}
              to="/atendimentos"
              display="flex"
              align="center"
              py="1"
            >
              <Icon as={RiDashboardLine} fontSize="20" />
              <Text ml="4" fontWeight="medium">
                Atendimentos
              </Text>
            </Link>
          </Stack>
        </Box>
        {/* <Box> */}
        {/*  <Text fontWeight="bold">Sistema</Text> */}
        {/*  <Stack spacing="4" mt="8" align="stretch"> */}
        {/*    <Link display="flex" align="center" py="1"> */}
        {/*      <Icon as={GrConfigure} fontSize="20" color="white" /> */}
        {/*      <Text ml="4" fontWeight="medium"> */}
        {/*        Configurações */}
        {/*      </Text> */}
        {/*    </Link> */}
        {/*  </Stack> */}
        {/* </Box> */}
      </Stack>
    </div>
  )
}
export default SidebarNav
