import React, { useState, useEffect, useRef, useCallback } from "react"
import {
  Box,
  Flex,
  Center,
  Grid,
  Heading,
  Stack,
  Image,
  Text,
  HStack,
} from "@chakra-ui/react"
import { Row, PageHeader, Descriptions, Button } from "antd"
import Webcam from "react-webcam"
import { useParams, useHistory } from "react-router-dom"
import { ImagePicker } from "../../components"
import api from "../../services/api"
import "./styles.css"

const Atendimento = () => {
  const history = useHistory()
  const webcamRef = useRef(null)
  const { id } = useParams()
  const [selectedPaciente, setSelectedPaciente] = useState()
  const [audio, setAudio] = useState(false)
  const [images, setImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])

  useEffect(() => {
    api
      .get(`paciente/${id}`)
      .then((res) => setSelectedPaciente(res.data))
      .catch((err) => console.log(err))
  }, [id, selectedPaciente])

  useEffect(() => {
    console.log("selImages", selectedImages)
  }, [selectedImages])

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current?.getScreenshot()
    setImages((img) => [...img, imageSrc])
  }, [webcamRef])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }
  function handleCreateReport() {
    console.log("report", selectedImages)
    history.push("/laudo")
  }

  function handleCleanImages() {
    setImages([])
  }

  const routes = [
    {
      path: "index",
      breadcrumbName: "First-level Menu",
    },
    {
      path: "first",
      breadcrumbName: "Second-level Menu",
    },
    {
      path: "second",
      breadcrumbName: "Third-level Menu",
    },
  ]

  return (
    <Row>
      <div className="site-page-header-ghost-wrapper">
        <PageHeader
          ghost={false}
          onBack={() => window.history.back()}
          title="Atendimento"
          subTitle="This is a subtitle"
        />
      </div>
      <Box borderRadius="10" bg="gray.500" w="100%" mb={6} p={2}>
        <Text fontWeight="sm" color="gray.300">
          Paciente:
        </Text>
      </Box>
      <Grid templateColumns="repeat(2, 1fr)" gap={5} h="100%">
        <Box borderRadius="10">
          <Webcam audio={audio} ref={webcamRef} screenshotFormat="image/jpeg" />
          <HStack py={2} spacing={4}>
            <Button onClick={() => capture()}>Capturar </Button>
            <Button onClick={() => capture()}>Gravar Vídeo </Button>
            {selectedImages ? (
              <Button onClick={() => handleCreateReport()}>Emitir Laudo</Button>
            ) : null}
          </HStack>
        </Box>
        <Flex bg="gray.500" borderRadius="10">
          <ImagePicker
            images={images.map((image, i) => ({ src: image, value: i }))}
            multiple
            onPick={(selImgs) => handleSelectedImages(selImgs)}
          />
        </Flex>
      </Grid>
    </Row>
  )
}

export default Atendimento
