import React, { useEffect, useState } from "react"
import {
  Row,
  Col,
  PageHeader,
  Card,
  Button,
  Input,
  Space,
  Select,
  Empty,
  Form,
  Dropdown,
  Menu,
} from "antd"
import { useHistory, useLocation } from "react-router-dom"
import { FileTextOutlined } from "@ant-design/icons"
import { Editor, EditorState } from "draft-js"
import { ImagePicker } from "../../components"

const { TextArea } = Input

const Laudo = () => {
  const navigation = useHistory()
  const location = useLocation()
  const [component, setComponent] = useState([])
  const [laudoImages, setLaudoImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])
  const [editorState, setEditorState] = useState(EditorState.createEmpty)
  const [text, setText] = useState("testesss")

  useEffect(() => {
    setLaudoImages(location.state.images)
  }, [location])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }

  const menu = (
    <Menu>
      <Menu.Item
        onClick={() => setText("1. Teste\n 2. Teste \n 3. Teste")}
        key="2"
      >
        Endoscopia
      </Menu.Item>
    </Menu>
  )

  return (
    <Row>
      <Col span={24}>
        <PageHeader onBack={() => window.history.back()} title="Emitir Laudo" />
      </Col>

      <Col span={24}>
        <h1>Dados do Paciente</h1>
      </Col>

      <Row gutter={[24]}>
        <Col span={12}>
          <Card title="TÃ³picos">
            <Editor editorState={handleEditorState} />
          </Card>
        </Col>

        <Col span={12}>
          <Card
            title="Imagens"
            bodyStyle={{ overflowY: "scroll", maxHeight: "500px" }}
          >
            {laudoImages.length >= 1 ? (
              <ImagePicker
                images={laudoImages.map((image, i) => ({
                  src: image.src,
                  value: i,
                }))}
                multiple
                onPick={(selImgs) => handleSelectedImages(selImgs)}
              />
            ) : (
              <Empty />
            )}
          </Card>
        </Col>
      </Row>
    </Row>
  )
}

export default Laudo
