import React, { useEffect, useState } from "react"
import {
  Row,
  Col,
  PageHeader,
  Card,
  Button,
  Input,
  Space,
  Select,
  Empty,
} from "antd"
import { useHistory, useLocation } from "react-router-dom"
import { ImagePicker } from "../../components"

const { TextArea } = Input

const Component = ({ index }) => {
  const { Option } = Select

  function onChange(value) {
    console.log(`selected ${value}`)
  }

  function onBlur() {
    console.log("blur")
  }

  function onFocus() {
    console.log("focus")
  }

  function onSearch(val) {
    console.log("search:", val)
  }

  return (
    <Col span={24}>
      <Space style={{ paddingBottom: 10, paddingTop: 10 }}>
        <Select
          showSearch
          style={{ width: 200 }}
          placeholder="Selecionar Topico"
          optionFilterProp="children"
          onChange={onChange}
          onFocus={onFocus}
          onBlur={onBlur}
          onSearch={onSearch}
          filterOption={(input, option) =>
            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
          }
        >
          <Option value={index}>Faringe</Option>
          <Option value="lucy">Laringe</Option>
          <Option value="tom">Traqueia</Option>
        </Select>
      </Space>
      <TextArea rows={4} id />
    </Col>
  )
}

const Laudo = () => {
  const navigation = useHistory()
  const location = useLocation()
  const [component, setComponent] = useState([])
  const [laudoImages, setLaudoImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])

  useEffect(() => {
    console.log("loc", location.state.images)
    setLaudoImages(location.state.images)
  }, [location])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }

  return (
    <Row>
      <Col span={24}>
        <PageHeader onBack={() => window.history.back()} title="Emitir Laudo" />
      </Col>
      <Col span={12}>
        {laudoImages.length >= 1 ? (
          <ImagePicker
            images={laudoImages.map((image, i) => ({
              src: image.src,
              value: i,
            }))}
            multiple
            onPick={(selImgs) => handleSelectedImages(selImgs)}
          />
        ) : (
          <Empty />
        )}
      </Col>
      <Col span={12}>
        <Card
          title="Tópicos"
          extra={
            <Button onClick={() => setComponent(component + 1)}>
              Adicionar Tópico
            </Button>
          }
        >
          {[...component].map((_, index) => (
            // eslint-disable-next-line react/no-array-index-key
            <Component key={index} index={index} />
          ))}
          <Col span={24}>
            <Button> Emitir Laudo </Button>
          </Col>
        </Card>
      </Col>
    </Row>
  )
}

export default Laudo
