import React from "react"
import { Box, Icon, Link, Stack, Text } from "@chakra-ui/react"
import { Link as RDomLink } from "react-router-dom"
import { RiDashboardLine } from "react-icons/ri"
import { GrConfigure } from "react-icons/gr"
import "./styles.css"

const SidebarNav = () => {
  const sidebar = document.querySelector(".sidebar")
  const mainContent = document.querySelector(".main-content")

  const handleSelectMenu = () => (
    // eslint-disable-next-line no-sequences
    sidebar.classList.toggle("sidebar_small"),
    mainContent.classList.toggle("main-content_large")
  )
  return (
    <div className="grid-container sidebar_small">
      <Stack spacing="12" align="flex-start">
        <Text fontSize="3xl" fontWeight="bold" letterSpacing="tight" w="64">
          Sisac Image
        </Text>
        <Box>
          <Text fontWeight="bold">Consultório</Text>
          <Stack spacing="4" mt="8" align="stretch">
            <Link
              as={RDomLink}
              to="/atendimentos"
              display="flex"
              align="center"
              py="1"
            >
              <Icon as={RiDashboardLine} fontSize="20" />
              <Text ml="4" fontWeight="medium">
                Atendimentos
              </Text>
            </Link>
          </Stack>
        </Box>
        {/* <Box> */}
        {/*  <Text fontWeight="bold">Sistema</Text> */}
        {/*  <Stack spacing="4" mt="8" align="stretch"> */}
        {/*    <Link display="flex" align="center" py="1"> */}
        {/*      <Icon as={GrConfigure} fontSize="20" color="white" /> */}
        {/*      <Text ml="4" fontWeight="medium"> */}
        {/*        Configurações */}
        {/*      </Text> */}
        {/*    </Link> */}
        {/*  </Stack> */}
        {/* </Box> */}
      </Stack>
    </div>
  )
}
export default SidebarNav
