import React, { useCallback, useEffect, useRef, useState } from "react"
import {
  Button,
  Card,
  Col,
  Descriptions,
  Empty,
  Row,
  Space,
  Tooltip,
} from "antd"
import { CameraOutlined } from "@ant-design/icons"
import Webcam from "react-webcam"
import { useHistory, useParams } from "react-router-dom"
import { ImagePicker } from "../../components"
import api from "../../services/api"
import "./styles.css"

const Atendimento = () => {
  const history = useHistory()
  const webcamRef = useRef(null)
  const { id } = useParams()
  const [selectedPaciente, setSelectedPaciente] = useState()
  const [audio, setAudio] = useState(false)
  const [images, setImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])

  useEffect(() => {
    api
      .get(`paciente/${id}`)
      .then((res) => setSelectedPaciente(res.data))
      .catch((err) => console.log(err))
  }, [id, selectedPaciente])

  useEffect(() => {
    console.log("selImages", selectedImages)
  }, [selectedImages])

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current?.getScreenshot()
    setImages((img) => [...img, imageSrc])
  }, [webcamRef])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }

  function handleCreateReport() {
    history.push("/laudo")
  }

  return (
    <Row span={24}>
      <Row span={24}>
        <Descriptions
          title="Dados do Paciente"
          extra={<Button type="primary">Salvar para Capturar Posterior</Button>}
        >
          <Descriptions.Item label="Nome">Zhou Maomao</Descriptions.Item>
          <Descriptions.Item label="Telefone">1810000000</Descriptions.Item>
        </Descriptions>
      </Row>
      <Row gutter={[12, 12]}>
        <Col span={12}>
          <Card
            title={
              <Tooltip>
                <span>Captura</span>
              </Tooltip>
            }
            bodyStyle={{ padding: 0 }}
          >
            <Webcam
              audio={audio}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              width="100%"
            />
            <Space style={{ padding: 10 }}>
              <Button onClick={() => capture()}>
                Capturar Imagem <CameraOutlined />
              </Button>
              <Button onClick={() => capture()}>Gravar Vídeo </Button>
              <Button onClick={() => handleCreateReport()}>Emitir Laudo</Button>
            </Space>
          </Card>
        </Col>
        <Col span={12}>
          <Card
            title="Imagens Capturadas"
            bodyStyle={{ overflowY: "scroll", maxHeight: "500px" }}
          >
            {images.length > 1 ? (
              <ImagePicker
                images={images.map((image, i) => ({
                  src: image,
                  value: i,
                }))}
                multiple
                onPick={(selImgs) => handleSelectedImages(selImgs)}
              />
            ) : (
              <Empty />
            )}
          </Card>
        </Col>
      </Row>
    </Row>
  )
}

export default Atendimento
