import React, { useState, useEffect, useRef, useCallback } from "react"
import {
  Box,
  Flex,
  Center,
  Grid,
  Heading,
  Stack,
  Image,
  Text,
  HStack,
} from "@chakra-ui/react"
import {
  Row,
  PageHeader,
  Descriptions,
  Button,
  Col,
  Divider,
  Space,
  Card,
} from "antd"
import Webcam from "react-webcam"
import { useParams, useHistory } from "react-router-dom"
import { ImagePicker } from "../../components"
import api from "../../services/api"
import "./styles.css"

const Atendimento = () => {
  const history = useHistory()
  const webcamRef = useRef(null)
  const { id } = useParams()
  const [selectedPaciente, setSelectedPaciente] = useState()
  const [audio, setAudio] = useState(false)
  const [images, setImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])

  useEffect(() => {
    api
      .get(`paciente/${id}`)
      .then((res) => setSelectedPaciente(res.data))
      .catch((err) => console.log(err))
  }, [id, selectedPaciente])

  useEffect(() => {
    console.log("selImages", selectedImages)
  }, [selectedImages])

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current?.getScreenshot()
    setImages((img) => [...img, imageSrc])
  }, [webcamRef])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }
  function handleCreateReport() {
    history.push("/laudo")
  }

  return (
    <Row span={24}>
      <Row gutter={[12, 12]}>
        <Col span={12}>
          <div>
            <Card title="Capturar Imagens">
              <Webcam
                audio={audio}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                height={500}
                width={500}
              />
            </Card>
            <Space>
              <Button onClick={() => capture()}>Capturar </Button>
              <Button onClick={() => capture()}>Gravar VÃ­deo </Button>
              {selectedImages.lenght >= 1 && (
                <Button onClick={() => handleCreateReport()}>
                  Emitir Laudo
                </Button>
              )}
            </Space>
          </div>
        </Col>
        <Col span={12}>
          <Col span={24}>
            <div
              style={{
                width: "100%",
                height: "100%",
                backgroundColor: "red",
              }}
            >
              <Center bg="gray.500" p={8} borderRadius={10}>
                <ImagePicker
                  images={images.map((image, i) => ({
                    src: image,
                    value: i,
                  }))}
                  multiple
                  onPick={(selImgs) => handleSelectedImages(selImgs)}
                />
              </Center>
            </div>
          </Col>
        </Col>
      </Row>
    </Row>
  )
}

export default Atendimento
