import React, { useState, useEffect, useRef, useCallback } from "react"
import {
  Box,
  Flex,
  Center,
  Grid,
  Heading,
  Stack,
  Image,
  Text,
  HStack,
} from "@chakra-ui/react"
import { Row, PageHeader, Descriptions, Button, Col, Divider } from "antd"
import Webcam from "react-webcam"
import { useParams, useHistory } from "react-router-dom"
import { ImagePicker } from "../../components"
import api from "../../services/api"
import "./styles.css"

const Atendimento = () => {
  const history = useHistory()
  const webcamRef = useRef(null)
  const { id } = useParams()
  const [selectedPaciente, setSelectedPaciente] = useState()
  const [audio, setAudio] = useState(false)
  const [images, setImages] = useState([])
  const [selectedImages, setSelectedImages] = useState([])

  useEffect(() => {
    api
      .get(`paciente/${id}`)
      .then((res) => setSelectedPaciente(res.data))
      .catch((err) => console.log(err))
  }, [id, selectedPaciente])

  useEffect(() => {
    console.log("selImages", selectedImages)
  }, [selectedImages])

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current?.getScreenshot()
    setImages((img) => [...img, imageSrc])
  }, [webcamRef])

  function handleSelectedImages(imgs) {
    setSelectedImages(imgs)
  }
  function handleCreateReport() {
    history.push("/laudo")
  }

  return (
    <Row span={18}>
      <Col span={18}>
        <Text fontWeight="sm" color="gray.300">
          Paciente:
        </Text>
      </Col>
      <Row gutter={[8, 8]}>
        <Col span={12}>
          <Box bg="gray.500" width="100%" p={10} borderRadius={10}>
            <Webcam
              audio={audio}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
            />
            <Row>
              <Button onClick={() => capture()}>Capturar </Button>
              <Button onClick={() => capture()}>Gravar Vídeo </Button>
              {selectedImages ? (
                <Button onClick={() => handleCreateReport()}>
                  Emitir Laudo
                </Button>
              ) : null}
            </Row>
          </Box>
        </Col>
        <Col span={12}>
          <Row>
            <Col span={24}>
              <Center bg="gray.500" p={8} borderRadius={10}>
                <ImagePicker
                  images={images.map((image, i) => ({ src: image, value: i }))}
                  multiple
                  onPick={(selImgs) => handleSelectedImages(selImgs)}
                />
              </Center>
            </Col>
          </Row>
        </Col>
      </Row>
    </Row>
  )
}

export default Atendimento
